package problems100;

import java.util.HashMap;
import java.util.HashSet;

public class judgeCircle {

    public static void main(String[] args){
        String moves = new String("");
        System.out.println(application2(moves));
    }

    private static boolean application(String moves){
        HashMap<Character,Integer> word = new HashMap<>();

        word.put('L',-1);
        word.put('R',1);
        word.put('U',2);
        word.put('D',-2);

        int len = moves.length();
        HashSet<Integer> res = new HashSet<>();
        for (int i=0;i<len;i++){
            for (int j=0;j<len;j++){
                if (!res.contains(i) && !res.contains(j) &&
                        word.get(moves.charAt(i))+word.get(moves.charAt(j))==0){
                    res.add(i);
                    res.add(j);
                }
            }
        }
        return res.size()==len;
    }

    private static boolean application2(String moves){
        HashMap<Character, Integer> res = new HashMap<>();
        res.put('L',0);
        res.put('R',0);
        res.put('U',0);
        res.put('D',0);

        for (int i=0;i<moves.length();i++){
            int temp = res.get(moves.charAt(i))+1;
            res.put(moves.charAt(i),temp);
        }

        return res.get('L').equals(res.get('R')) && res.get('U').equals(res.get('D'));
    }
}
